ruleSearch.bookList:
class.SHsectionThree-middle.-1@p<js>
result
   .toArray()
   .slice(0, 3)
   .map(function (el){
    var url = String(el.select('a').eq(1).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      String(J.select('[property="og:' + selector + '"]').attr('content'));

    return {
      name: og('novel:book_name').replace(/(全文|免费|最新章节).*|（.*）/,''),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/,''),
      latest: og('novel:lastest_chapter_name').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'').replace(/^(正文)?(\s+|_)/,'') + ' • ' + og('novel:update_time').replace(/\s.*/,''),
      cover: og('image'),
      intro: og('description').replace(/.*简介：|各位书友.*/,'').replace(/\s+/g,''),
      url: url,
    };
  });
</js>

