ChapterContent@js:
function p(e) {
    return e.split("").map(function (e) {
        return e.match(/[A-Za-z]/) ? (c = Math.floor(e.charCodeAt(0) / 97), k = (e.toLowerCase().charCodeAt(0) - 83) % 26 || 26, String.fromCharCode(k + (0 == c ? 64 : 96))) : e
    }).join("")
}
java.base64Decode(p(result))

ruleSearch.bookList:
#sitembox dl
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name'),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/, ''),
    wordCount: o('.count ul li:eq(3) span').replace(/K/,'000'),
		latest: og('novel:latest_chapter_name') + '▪' + og('novel:update_time').replace(/\//g, '-').match(/\D(\d{2}-\d{2})/)[1],
		intro: o('.wrapper').replace(/\s/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

