ruleToc.chapterList:
class.chapterlist.-1@ul@li@a
<js>
List=[];
sectionPage=result;
aid=baseUrl.match(/\/\d+\/(\d+)\//)[1];
option={
	"method":"POST",
	"body":"more="+1+"&aid="+aid
	};
url='https://www.ah123z.com/modules/article/reader.php,'+JSON.stringify(option)
result=java.ajax(url);
result=result.split("\</li\>").map(x=>x+"</li>");
for(i=0;i<=sectionPage.length-1;i++){
		if(i!==30){
			List.push({
		    "name":sectionPage[i].text(),
		    "link":baseUrl.replace(/www/,'m')+sectionPage[i].attr('href')
		  })
		}else{
				for(j=0;j<=result.length-1;j++){
			 	 List.push({
		   "name":result[j].match(/(href=.*\>(.*?)\<\/a\>)/)[2],
		     "link":baseUrl.replace(/www/,'m')+result[j].match(/href="(.*)"/)[1]
		    })
			 	}
			}
	}
	List
</js>

