ruleToc.chapterList:
@css:#list dl > *
@js:
a=result
list=[]
l=0
for(i=0;i<a.length;i++){
	java.setContent(a[i])
	b=java.getElements("dt")
	if(b!=""){
	l++
	}
	if(l>=2&&b==""){
		list.push(a[i])
		}
	}
list

